mapper.select:
SELECT SEQ, ID, TITLE, CONTENT, MDATE, REGDATE 
FROM CALBOARD 
WHERE SEQ = #{seq}

mapper.update:
UPDATE CALBOARD SET
TITLE = #{title}, CONTENT = #{content}, MDATE = #{mdate}, REGDATE = SYSDATE
WHERE SEQ = #{seq}

mapper.select:
SELECT COUNT(*)
FROM CALBOARD
WHERE ID = #{id} AND SUBSTR(MDATE,1,8) = #{yyyyMMdd}

SELECT RN, SEQ, ID, TITLE, CONTENT, MDATE, REGDATE
FROM	(SELECT ROW_NUMBER() OVER(PARTITION BY SUBSTR(MDATE,1 ,8) ORDER BY MDATE) AS RN, SEQ, ID, TITLE, CONTENT, MDATE, REGDATE 
		 FROM CALBOARD
		 WHERE ID = #{id} AND SUBSTR(MDATE,1 ,6) = #{yyyyMM}
		)
WHERE RN BETWEEN 1 AND 3
